from itertools import product

def calculate_arbitrage(odds_combination):
    total_implied_probability = sum(1 / odd for odd in odds_combination)
    arbitrage_percentage = (1 - total_implied_probability) / total_implied_probability * 100
    return arbitrage_percentage, total_implied_probability

def find_best_combination(bookmaker_odds):
    best_combination = None
    best_arbitrage_percentage = 0

    for odds_combination in product(*bookmaker_odds):
        arbitrage_percentage, total_implied_probability = calculate_arbitrage(odds_combination)

        if arbitrage_percentage > best_arbitrage_percentage:
            best_arbitrage_percentage = arbitrage_percentage
            best_combination = odds_combination

        print(f"Combination: {odds_combination}, Implied Probability: {total_implied_probability:.2f}%, Arbitrage Percentage: {arbitrage_percentage:.2f}%")

    return best_combination, best_arbitrage_percentage

